mapper.update:
UPDATE ${shardTableName} 
SET SUCCESS_TIMES = SUCCESS_TIMES +
#{successTimes},
FAIL_TIMES=FAIL_TIMES+#{failTimes},

	ELAPSED_AVG =
	(ELAPSED_AVG*SUCCESS_TIMES+#{elapsedAvg}*#{successTimes})/(SUCCESS_TIMES+#{successTimes}),

ELAPSED_MAX=GREATEST(ELAPSED_MAX,#{elapsedMax}),
SUCCESS_TIMES_DETAIL=CONCAT(SUCCESS_TIMES_DETAIL,#{successTimesDetail}),
FAIL_TIMES_DETAIL=CONCAT(FAIL_TIMES_DETAIL,#{failTimesDetail}),
ELAPSED_TOTAL_DETAIL=CONCAT(ELAPSED_TOTAL_DETAIL,#{elapsedTotalDetail}),
ELAPSED_MAX_DETAIL=CONCAT(ELAPSED_MAX_DETAIL,#{elapsedMaxDetail})
where APP_NAME=#{appName}
AND SERVICE = #{service}
AND CONSUMER_IP = #{consumerIP}
AND PROVIDER_IP = #{providerIP}
AND TIME = #{time}

mapper.select:
select
ID_,APP_NAME,SERVICE,CONSUMER_IP,PROVIDER_IP,TIME,SUCCESS_TIMES,FAIL_TIMES,ELAPSED_AVG,ELAPSED_MAX,SUCCESS_TIMES_DETAIL,FAIL_TIMES_DETAIL,ELAPSED_TOTAL_DETAIL,ELAPSED_MAX_DETAIL
from ${shardTableName}
where 1=1

	AND APP_NAME = #{appName}


	AND SERVICE = #{service}


	AND CONSUMER_IP = #{consumerIP}


	AND PROVIDER_IP = #{providerIP}


	AND TIME = #{time}

	select
	ID_,APP_NAME,SERVICE,CONSUMER_IP,PROVIDER_IP,TIME,SUCCESS_TIMES,FAIL_TIMES,ELAPSED_AVG,ELAPSED_MAX,SUCCESS_TIMES_DETAIL,FAIL_TIMES_DETAIL,ELAPSED_TOTAL_DETAIL,ELAPSED_MAX_DETAIL
	from ${shardTableName}
	where 1=1


    	AND TIME = #{time}




    	AND TIME >= #{time_from}




    	AND TIME <= #{time_to}



		AND APP_NAME = #{appName}


		AND SERVICE = #{service}


		AND CONSUMER_IP = #{consumerIP}


		AND PROVIDER_IP = #{providerIP}

mapper.update:
UPDATE ${shardTableName} 
SET SUCCESS_TIMES = #{successTimes},
FAIL_TIMES=#{failTimes},
ELAPSED_AVG =#{elapsedAvg},
ELAPSED_MAX=#{elapsedMax},
SUCCESS_TIMES_DETAIL=#{successTimesDetail},
FAIL_TIMES_DETAIL=#{failTimesDetail},
ELAPSED_TOTAL_DETAIL=#{elapsedTotalDetail},
ELAPSED_MAX_DETAIL=#{elapsedMaxDetail}
where APP_NAME=#{appName}
AND SERVICE = #{service}
AND CONSUMER_IP = #{consumerIP}
AND PROVIDER_IP = #{providerIP}
AND TIME = #{time}

mapper.select:
select SERVICE ,sum(SUCCESS_TIMES) SUCCESS_TIMES,sum(FAIL_TIMES) FAIL_TIMES,sum(SUCCESS_TIMES*ELAPSED_AVG)/sum(SUCCESS_TIMES) ELAPSED_AVG,max(ELAPSED_MAX) ELAPSED_MAX 
from ${shardTableName} 
where 1=1

	AND APP_NAME = #{appName}


	AND SERVICE = #{service}


	AND TIME = #{time}

group by SERVICE
